<script>
import { onMounted } from 'vue'
import { gsap, Power2 } from 'gsap'
import { ScrollTrigger } from 'gsap/ScrollTrigger'

export default {
  setup () {
    onMounted(() => {
      const Summer = gsap.timeline()
      ScrollTrigger.create({
        animation: Summer,
        trigger: '.scrollElement',
        start: '2% top',
        end: '50% 100%',
        scrub: 3
      })
      gsap.fromTo('#openeyes, #ropeneyes', { opacity: 0, duration: 0.2, ease: 'none', repeatDelay: 4.9, repeat: -1 }, { opacity: 1, duration: 0.2, ease: 'none', repeatDelay: 4.9, repeat: -1 })
      gsap.fromTo('#openeyes, #ropeneyes', { opacity: 0, duration: 0.2, ease: 'none', delay: 0.2, repeatDelay: 4.9, repeat: -1 }, { opacity: 1, duration: 0.2, ease: 'none', delay: 0.2, repeatDelay: 4.9, repeat: -1 })
      gsap.fromTo('#splash1', { opacity: 0, scale: 0, autoAlpha: 0, duration: 1, ease: Power2, transformOrigin: '50% 50%', repeat: -1, repeatDelay: 4, delay: 1 }, { opacity: 1, scale: 15, autoAlpha: 15, duration: 1, ease: Power2, transformOrigin: '50% 50%', repeat: -1, repeatDelay: 4, delay: 1 })
      gsap.to('#splash1', { opacity: 0, duration: 1, repeat: -1, repeatDelay: 4, delay: 1.5 })
      gsap.fromTo('#splash2', { opacity: 0, scale: 0, autoAlpha: 0, duration: 1, ease: Power2, transformOrigin: '50% 50%', repeat: -1, repeatDelay: 4, delay: 2 }, { opacity: 1, scale: 15, autoAlpha: 15, duration: 1, ease: Power2, transformOrigin: '50% 50%', repeat: -1, repeatDelay: 4, delay: 2.5 })
      gsap.to('#splash2', { opacity: 0, duration: 1, repeat: -1, repeatDelay: 4, delay: 3 })
      gsap.fromTo('#splash3', { opacity: 0, scale: 0, autoAlpha: 0, duration: 1, ease: Power2, transformOrigin: '50% 50%', repeat: -1, repeatDelay: 4, delay: 3 }, { opacity: 1, scale: 15, autoAlpha: 15, duration: 1, ease: Power2, transformOrigin: '50% 50%', repeat: -1, repeatDelay: 4, delay: 4 })
      gsap.to('#splash3', { opacity: 0, duration: 1, repeat: -1, repeatDelay: 4, delay: 4.5 })
      Summer.fromTo('#rain1', 5, { opacity: 0, y: '-100%' }, { opacity: 1, y: '200%' }, 0)
      Summer.fromTo('#rain2', 5, { opacity: 0, y: '-100%' }, { opacity: 1, y: '100%' }, 0)
    })
  }
}
</script>

